@prefix bot:  <https://w3id.org/bot#> .
@prefix rdf:  <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix inst: <http://example.org/botTest#> .

##############
# ABOX
##############
inst:SiteA
	a bot:Site ;
	bot:hasBuilding inst:BuildingA .

inst:BuildingA
	a bot:Building ;
	bot:hasStorey inst:Storey00 , inst:Storey01 .

inst:Storey00
	a bot:Storey ;
	bot:hasSpace inst:SpaceA , inst:SpaceB .

inst:Storey01
	a bot:Storey ;
	bot:hasSpace inst:SpaceC , inst:SpaceD .

inst:SpaceA
	a bot:Space .

inst:SpaceB
	a bot:Space .

inst:SpaceC
	a bot:Space .

inst:SpaceD
	a bot:Space .

##############
# BOT SIMPLIFIED
##############
bot:Zone 
	a owl:Class .
bot:Site 
	a owl:Class ;
	rdfs:subClassOf bot:Zone .
bot:Building 
	a owl:Class ;
	rdfs:subClassOf bot:Zone .
bot:Storey 
	a owl:Class ;
	rdfs:subClassOf	bot:Zone .
bot:Space 
	a owl:Class ;
	rdfs:subClassOf	bot:Zone .
bot:Element 
	a owl:Class .
bot:Interface 
	a owl:Class .

bot:containsZone 
	a owl:ObjectProperty , owl:TransitiveProperty ;
	rdfs:domain	bot:Zone ;
        rdfs:range      bot:Zone .
bot:adjacentZone 
	a owl:ObjectProperty , owl:SymmetricProperty ;
	rdfs:domain     bot:Zone ;
        rdfs:range      bot:Zone .
bot:hasBuilding 
	a owl:ObjectProperty ;
	rdfs:subPropertyOf bot:containsZone ;
	rdfs:domain     bot:Zone ;
        rdfs:range      bot:Building .				
bot:hasStorey 
	a owl:ObjectProperty ;
	rdfs:subPropertyOf bot:containsZone ;
	rdfs:domain     bot:Zone ;
        rdfs:range      bot:Storey .
bot:hasSpace 
	a owl:ObjectProperty ;
	rdfs:subPropertyOf bot:containsZone ;
	rdfs:domain     bot:Zone ;
        rdfs:range      bot:Space .	
bot:adjacentElement 
	a owl:ObjectProperty ;
	rdfs:domain     bot:Zone ;
        rdfs:range      bot:Element .
bot:containsElement 
	a owl:ObjectProperty ;
	rdfs:domain     bot:Zone ;
        rdfs:range      bot:Element ;
	owl:propertyChainAxiom  ( bot:containsZone bot:containsElement ) .
bot:hostsElement 
	a owl:ObjectProperty ;
	rdfs:domain     bot:Element ;
        rdfs:range      bot:Element .
bot:interfaceOf 
	a owl:ObjectProperty ;
	rdfs:domain     bot:Interface .

[] a owl:AllDisjointClasses ;
        owl:members
              ( 
		bot:Site
		bot:Element
                bot:Space
                bot:Storey
                bot:Building ) .
[] a owl:AllDisjointClasses ;
        owl:members
              ( 
		bot:Zone
		bot:Element ) .
bot:Interface rdfs:subClassOf [
    a owl:Restriction ;
    owl:onProperty bot:interfaceOf ;
    owl:cardinality 2
] .

